import React from 'react'
import { useLocation } from 'react-router-dom';
import "../css/allwarehouse.css";
import { useState, useEffect } from 'react';


function AllWarehouse() {

  const location = useLocation();
  const album = location.pathname;
  const str = album.lastIndexOf('/');
  const id = album.slice(str+1);

  const [warehouse, setWarehouse] = useState([]);
  const [warehousedetails, setWarehousedetails] = useState();

  useEffect(() => {
    import('../db/db.json')
      .then(data => {
        setWarehouse(data.warehouse);
      })
      .catch(error => {
        console.error('Error fetching local JSON data:', error);
      });
  }, []);

  function searchdata(){
    let length = warehouse.length();
    for (let i=0; i<length;i++)
    {
      if(warehouse[i].id === id)
      {
        setWarehousedetails(warehouse[i]);
      }
    }
  }

  console.log(warehousedetails)
  return (
    <div>
      <div className='allWarehouseMainContainer'>

      </div>
    </div>
  )
}

export default AllWarehouse
